import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function ActionCard({ title, desc, icon, path }) {
  const navigate = useNavigate();

  const handleClick = () => {
    if (path) {
      navigate(path);
    }
  };

  return (
    <div className="action-card" onClick={handleClick} style={{ cursor: 'pointer' }}>
      <div className="icon">{icon}</div>
      <h4>{title}</h4>
      <p>{desc}</p>
    </div>
  );
}
